var paramsResult = {}
var isDirty = false
var previousSelectedDB = ""
var previousSelectedJob = ""
function deleteInstanceRecord(r){
	var i= r.parentNode.parentNode.rowIndex;
	var row = r.parentNode.parentNode
	document.getElementById('instanceParamTable').deleteRow(i);
	var instanceName = row.cells[0].innerText || row.cells[0].textContent
	delete paramsResult.instances[instanceName]
        isDirty = true
   }
require(['jquery'],function($){


   function resetControl(){
      $("#message").text("")
      $("#jobName").empty()
      $("#hostID").val("")
      $("#Sid").val("")
      $("#paramContent").empty()
      $("#InstanceParamList").empty()
      $("#instanceName").val("")
      $("#hostIDInstance").val("")
      $("#sidInstance").val("")
      $("#instanceSchedule").val('60i')
      $("#instanceParamTable").empty()
      $("#instanceParamTable").append("<tr><th><p>Instance's name</p></th><th><p>Params</p></th><th><p>Schedule</p></th><th><p>Delete instance</p></th></tr>")
      paramsResult = {}
   }

   $(document).ready(function(){
          var params = 'dbcommon'
          var dbtypeServlet = 'dbtype'
　　　　　　
　　　　　　$.ajax({
                type: "GET",
                url:"/xwiki/bin/get/WiperDog/GetServlet?outputSyntax=plain&name="+dbtypeServlet,
                success : function(dbtypeUrl){
                        var dbtypeUrl
                        processListDbtype(dbtypeUrl)
                },
                error: function(xhr,status,error){
                }
          });
          $.ajax({
                type: "GET",
                url:"/xwiki/bin/get/WiperDog/GetServlet?outputSyntax=plain&name="+params,
                success : function(result){
                        processData(result)

                },
                error: function(xhr,status,error){
                }
          });
    function processListDbtype(dbtypeUrl){
      //Send request to get list dbtype and bind to selectbox
      $.ajax({
                type: "GET",
                url: dbtypeUrl + "/DbType",
                success : function(mapDBType){
                        for(dbtype in mapDBType){
                           $("#dbTypeCbb").append("<option id=\"" + dbtype + "\" value=\"" + dbtype + "\">" + dbtype + "</option>")
                        }
                },
                error: function(xhr,status,error){
                        alert('Is Wiperdog agent on yet?')
                }
          });
      
    }

    function processData(domainURL) {
      $("#dbTypeCbb").change(function(){
          var choice = false
          if(isDirty){
             choice = confirm("There are modifications!Continue without saving?")
          }
          if(!choice && isDirty){
             $("#dbTypeCbb").val(previousSelectedDB)
             return
          } else {
             if(choice){
                isDirty = false
             }
          }
          var selectedDBType = $("#dbTypeCbb option:selected").val()
          previousSelectedDB = $("#dbTypeCbb option:selected").val()
          $("#listJob option:gt(0)").remove()
          resetControl()
          if(selectedDBType != null && selectedDBType != ""){
             $("#waiting").show()
             $.ajax({
                 type: "GET",
                 crossDomain: true,
                 url:domainURL + "?dbtype=" + selectedDBType,
                 dataType: 'json',
                 success : function(result){
                       for(var i = 0; i<result.length;i++){
                            $("#listJob").append("<option id=" + result[i] + " value=" + result[i] + ">" + result[i] + "</option>")
                       }
                       $("#waiting").hide()
                 },
                 error : function(xhr,status,error){
                       alert("There is an error! Wiperdog agent is on yettttttttttttt?")
                       $("#waiting").hide()
                       console.log(status)
                       console.log(error)
                       console.log(xhr)
                 },
                 "Access-Control-Allow-Origin":domainURL
               });
          }

      });

      $("#listJob").change(function(){
         var choice = false
         if(isDirty){
            choice = confirm("There are modifications!Continue without saving?")
         }
         if(!choice && isDirty){
            $("#listJob").val(previousSelectedJob)
            return
         } else {
            if(choice){
               isDirty = false
            }
         }
         previousSelectedJob = $("#listJob option:selected").val()
         resetControl()
         if($("#listJob option:selected").val() != ''){
            var jobName = $("#listJob option:selected").val()
            $("#waiting").show()
            $.ajax({
                 type: "GET",
                 crossDomain: true,
                 url:domainURL + "?jobName=" + jobName,
                 dataType: 'json',
                 success : function(result){
                       paramsResult = result
                       $("#jobName").text(paramsResult.jobName)
                       $("#paramContent").text(JSON.stringify(paramsResult.params))
                       var mapInstance = paramsResult.instances
                       if(!(mapInstance instanceof Array)){
                          for(var instanceKey in mapInstance){
                             var instance = mapInstance[instanceKey]
                             var strParam = instance.params != null ? JSON.stringify(instance.params) : ""
                             var strSchedule = instance.schedule != null ? instance.schedule : ""
                             $("#instanceParamTable").append("<tr><td><p>" + instanceKey + "</p></td><td><p>" + strParam + "</p></td><td><p>" + strSchedule + "</p></td><td><input type=\"submit\" value=\"delete\" class=\"commonBtn\" id=" + instanceKey + " onClick='deleteInstanceRecord(this)'></td></tr>")
                          }
                       }
                       $("#waiting").hide()
                 },
                 error : function(xhr,status,error){
                       alert("There is an error! Wiperdog agent is on yet?")
                       console.log(status)
                       console.log(error)
                       console.log(xhr)
                       $("#waiting").hide()
                 },
                 "Access-Control-Allow-Origin":domainURL
            });
         }
      });

      $("#updateForJobBtn").click(function(){
         var hostID = $("#hostID").val()
         if(hostID.indexOf(".") > 0){
            $("#message").text("Host ID can not contain punctuation character")
            $("#message").addClass("error").removeClass("message")
            return
         } else {
           $("#message").text("")
         }
         var sid = $("#Sid").val()
         if(sid != "" && hostID == "" ){
            $("#message").text("Host ID can not empty if Sid is valid")
            $("#message").addClass("error").removeClass("message")
            return
         } else {
           $("#message").text("")
         }
         if($("#dbTypeCbb option:selected").val() == ""){
             $("#message").text("DB Type is required")
            $("#message").addClass("error").removeClass("message")
             return
         } else {
           $("#message").text("")
         }
         if($("#listJob option:selected").val() == ""){
            $("#message").text("Job file is required")
            $("#message").addClass("error").removeClass("message")
             return
         } else {
           $("#message").text("")
         }
         if(hostID != null && hostID != ""){

            var mapData = {}
            mapData['dbHostId'] = hostID
            mapData['dbSid'] = sid
            paramsResult.params = mapData
            $("#paramContent").text(JSON.stringify(mapData))
            isDirty = true
         }
      });

      $("#updateForInstanceBtn").click(function(){
         if($("#dbTypeCbb option:selected").val() == ""){
             $("#message").text("DB Type is required")
            $("#message").addClass("error").removeClass("message")
             return
         } else {
           $("#message").text("")
         }

         if($("#listJob option:selected").val() == ""){
            $("#message").text("Job file is required")
            $("#message").addClass("error").removeClass("message")
             return
         } else {
           $("#message").text("")
         }

         var instanceName = $("#instanceName").val()
         if(instanceName == "" ){
            $("#message").text("Instances Name is required")
            $("#message").addClass("error").removeClass("message")
            return
         } else {
           $("#message").text("")
         }
         var hostIDInstance = $("#hostIDInstance").val()
         if(hostIDInstance.indexOf(".") > 0){
           $("#message").text("Host ID can not contain punctuation character")
            $("#message").addClass("error").removeClass("message")
            return
         } else {
           $("#message").text("")
         }
         var sidInstance = $("#sidInstance").val()
         if(sidInstance != "" && hostIDInstance == "" ){
           $("#message").text("Host ID can not empty if Sid is valid")
            $("#message").addClass("error").removeClass("message")
            return
         } else {
           $("#message").text("")
         }
         var instanceSchedule = ($("#instanceSchedule").val() != null && $("#instanceSchedule").val() != "")?$("#instanceSchedule").val():'60i'
         var finalMapData = {}
         if(instanceName != null && instanceName != ""){
            var mapData = {}
            if(hostIDInstance != null && hostIDInstance != ""){
               mapData['dbHostId'] = hostIDInstance
            }
            if(sidInstance != null && sidInstance != ""){
               mapData['dbSid'] = sidInstance
            }
            if(mapData != null && hostIDInstance != null && hostIDInstance != "" && sidInstance != null && sidInstance != ""){
               finalMapData['params'] = mapData
            }
            //if(paramsResult.instances[instanceName] == null){
            //   paramsResult.instances[instanceName] = {}
            //}
            //paramsResult.instances[instanceName] = finalMapData
            //paramsResult.instances[instanceName].schedule = instanceSchedule
            //$("#instanceParamContent").text(JSON.stringify(paramsResult.instances))
            if(paramsResult.instances[instanceName] == null){
                paramsResult.instances[instanceName] = {}
            }
            paramsResult.instances[instanceName].schedule = instanceSchedule
            for(data in mapData){
               if(paramsResult.instances[instanceName].params == null){
                  paramsResult.instances[instanceName].params = {}
               }
               paramsResult.instances[instanceName].params[data] = mapData[data]
            }
            $("#instanceParamTable").empty()
            $("#instanceParamTable").append("<tr><th><p>Instance's name</p></th><th><p>Params</p></th><th><p>Schedule</p></th><th><p>Delete instance</p></th></tr>")
            for(var instanceKey in paramsResult.instances){
                var instance = paramsResult.instances[instanceKey]
                var strParam = instance.params != null ? JSON.stringify(instance.params) : ""
                var strSchedule = instance.schedule != null ? instance.schedule : ""
                $("#instanceParamTable").append("<tr><td><p>" + instanceKey + "</p></td><td><p>" + strParam + "</p></td><td><p>" + strSchedule + "</p></td><td><input type=\"submit\" value=\"delete\" class=\"commonBtn\" id=" + instanceKey + " onClick=\"deleteInstanceRecord(this)\"></td></tr>")
            }
            isDirty = true
         }
      });

      $("#CancelBtn").click(function(){
         if(!isDirty){
            $("#dbTypeCbb").val("")
            $("#listJob").val("")
            resetControl()
         }else{
            //dialog box confirm if dirty
            var choice = confirm('Are you sure to cancel modification ?')
            if(choice){
               $("#dbTypeCbb").val("")
               $("#listJob").val("")
               resetControl()
               isDirty = false
            }
          }
      });

      $("#UpdateConfigBtn").click(function(){
         if(isDirty){
         var jobName = $("#listJob option:selected").val()
         var finalMapResult = {}

         finalMapResult['job'] = jobName
         finalMapResult['data'] = paramsResult
         if(paramsResult)
            $.ajax({
                 type: "POST",
                 crossDomain: true,
                 url:domainURL + "?jobName=" + jobName,
                 dataType: 'json',
                 data: JSON.stringify(finalMapResult),
                 success : function(result){
                       if(result.status == 'OK'){
                          $("#message").addClass("message").removeClass("error")
                          $("#message").text("Update succesfully !")
                          isDirty = false
                       }
                 },
                 error : function(xhr,status,error){
                       $("#message").addClass("message").removeClass("error")
                       $("#message").text("There is an error! Wiperdog agent is on yet?")
                       console.log(status)
                       console.log(error)
                       console.log(xhr)
                 },
                 async:false
            });
         }else {
            $("#message").text("There is no modification!").addClass('notice').removeClass('error').removeClass('message')
         }
      });
     }

   });
jQuery.noConflict();
