  function checkDirtyAll() {
    userName = $.trim($("#uName").val())
    password = $.trim($("#passwd").val())
    wdHome = $.trim($("#dir").val())
    remoteHost = $.trim($("#remoteHost").val())
    if (userName != "" || password != "" || wdHome != "" || remoteHost != "") {
	return true
    } else {
	return false
    }
