		hostname = arr_str[1].replace("//","")
		port = arr_str[2].substring(0,arr_str[2].indexOf("/"))
		servletPath = arr_str[2].substring(arr_str[2].indexOf("/")+1)
	}
	if(value.desc != null && value.desc != []){
		description = value.desc
	} else {
		description = "No description"
	}
