	}
println "<tr id='input'><td><input type='text' id = 'name' /></td><td><input type='text' id = 'host' /></td><td><input type='text' id = 'port' /></td><td><input type='text' id = 'servlet' /></td><td><textarea type='text' width='auto' id='desc'/></textarea></td></tr>"
println "</table>"
